mapper.select:
select * from ck_info


         name like '%${param1}%' or ckno like '%${param1}%' or sex like '%${param1}%'


 limit #{param2},#{param3}

mapper.update:
update ck_info SET
 name = #{name}, pno = #{pno}, height = #{height}, weight = #{weight},
 date = #{date}, jzno = #{yyno}, dno = #{dno} where id = #{id}
