mapper.select:
select * from medical


         name like '%${param1}%' or mcno like '%${param1}%' or sex like '%${param1}%'


 limit #{param2},#{param3}

mapper.update:
update medical SET
 name = #{name}, card = #{card}, type = #{type}, remark = #{remark},sex = #{sex}, age = #{age}, profession = #{profession},
 address = #{address},createdate = #{createdate}, phone = #{phone}, jzno = #{medicalcard.yyno}, ybno = #{medicarecard.ybno} where id = #{id}
