mapper.select:
select * from patient


         name like '%${param1}%' or pno like '%${param1}%' or sex like '%${param1}%'


 limit #{param2},#{param3}

mapper.update:
update patient SET
(pno=#{pno}, name = #{name}, password = #{password}, card = #{card}, sex = #{sex}, age = #{age}, profession = #{profession},
 address = #{address}, money = #{money},createdate = #{createdate}, phone = #{phone}, yyno = #{yyno}, ybno = #{ybno}, subscribe = #{subscribe}) where id = #{id}
