mapper.select:
select * from prescribe


        pno like '%${param1}%' or drugname like '%${param1}%'


limit #{param2},#{param3}

mapper.update:
update prescribe SET
name = #{name}, pno = #{pno}, height = #{height}, weight = #{weight},
date = #{date}, jzno = #{medicalcard.yyno}, dno = #{doctor.dno} where id = #{id}
