mapper.select:
select * from yk_wait order by `level`


         name like '%${param1}%' or wno like '%${param1}%' or sex like '%${param1}%'


 limit #{param2},#{param3}

mapper.update:
update yk_wait SET
 name = #{name}, pno = #{pno}, type = #{type}, remark = #{remark},sex = #{sex}, age = #{age}, level = #{level},
 date = #{date}, jzno = #{medicalcard.yyno}, dno = #{doctor.dno} where id = #{id}
